#!/bin/bash

# Generate simple placeholder PNG icons using base64

# 16x16 transparent purple square with email emoji overlay
echo "" | base64 -d > icon16.png

# 48x48 transparent purple square
echo "" | base64 -d > icon48.png

# 128x128 transparent purple square
echo "" | base64 -d > icon128.png

echo "✅ Created icon16.png"
echo "✅ Created icon48.png"  
echo "✅ Created icon128.png"
echo ""
echo "Placeholder icons created successfully!"
